package Survey;

public class ChoiceOption {
    private int id;
    private String label;

    public ChoiceOption(int id, String label) {
        this.id = id;
        this.label = label;
    }

    // Getters and setters
    public int getId() {
        return id;
    }

    public void setId(int id) {
        this.id = id;
    }

    public String getLabel() {
        return label;
    }

    public void setLabel(String label) {
        this.label = label;
    }
}

package Survey;

import java.util.*;

public class MultipleChoiceQuestion extends Question {
    private int minChoices;
    private int maxChoices;
    private List<ChoiceOption> options;

    public MultipleChoiceQuestion(int id, String text, boolean required, int position, double weight, int minChoices, int maxChoices) {
        super(id, text, required, position, weight);
        this.minChoices = minChoices;
        this.maxChoices = maxChoices;
        this.options = new ArrayList<>();
    }

    // Getters and setters
    public int getMinChoices() {
        return minChoices;
    }

    public void setMinChoices(int minChoices) {
        this.minChoices = minChoices;
    }

    public int getMaxChoices() {
        return maxChoices;
    }

    public void setMaxChoices(int maxChoices) {
        this.maxChoices = maxChoices;
    }

    public List<ChoiceOption> getOptions() {
        return options;
    }

    public void addOption(ChoiceOption option) {
        this.options.add(option);
    }
}

