package Survey;

import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;

public class OpenStringQuestion extends Question {
    private List<String> responses;

    public OpenStringQuestion(String text) {
        super(text);
        this.responses = new ArrayList<>();
    }

    @Override
    public void ask() {
        System.out.println(getText());
        Scanner scanner = new Scanner(System.in);
        String response = scanner.nextLine();
        responses.add(response);
    }

    @Override
    public void displayResults() {
        System.out.println("Responses to open question '" + getText() + "':");
        for (String response : responses) {
            System.out.println("- " + response);
        }
    }
}

package Survey;

public abstract class Question {
    private int id;
    private String text;
    private boolean required;
    private int position;
    private double weight;

    public Question(int id, String text, boolean required, int position, double weight) {
        this.id = id;
        this.text = text;
        this.required = required;
        this.position = position;
        this.weight = weight;
    }

    // Getters and setters
    public int getId() {
        return id;
    }

    public void setId(int id) {
        this.id = id;
    }

    public String getText() {
        return text;
    }

    public void setText(String text) {
        this.text = text;
    }

    public boolean isRequired() {
        return required;
    }

    public void setRequired(boolean required) {
        this.required = required;
    }

    public int getPosition() {
        return position;
    }

    public void setPosition(int position) {
        this.position = position;
    }

    public double getWeight() {
        return weight;
    }

    public void setWeight(double weight) {
        this.weight = weight;
    }
}

